changeSet.sql:
-- Create employee table based on the provided entity CREATE TABLE employees ( id BIGINT PRIMARY KEY GENERATED ALWAYS AS IDENTITY, supervisor_id BIGINT NOT NULL, name VARCHAR(100) NOT NULL, surname VARCHAR(100) NOT NULL, employee_role VARCHAR(50) NOT NULL, employee_status VARCHAR(50) NOT NULL, registration_date TIMESTAMP WITH TIME ZONE );
-- Create employee_credentials table CREATE TABLE employee_credentials ( id BIGINT PRIMARY KEY GENERATED ALWAYS AS IDENTITY, employee_id BIGINT NOT NULL UNIQUE, password_hash VARCHAR(255) NOT NULL, last_password_change TIMESTAMP WITH TIME ZONE, reset_token VARCHAR(255), reset_token_expiry TIMESTAMP WITH TIME ZONE, failed_attempt INT DEFAULT 0, CONSTRAINT fk_credentials_employee FOREIGN KEY (employee_id) REFERENCES employees (id) );
-- Create employee_contact_details table CREATE TABLE employee_contact_details ( id BIGINT PRIMARY KEY GENERATED ALWAYS AS IDENTITY, employee_id BIGINT NOT NULL UNIQUE, email VARCHAR(255) NOT NULL, phone_number VARCHAR(20) NOT NULL, address VARCHAR(255), city VARCHAR(100), postal_code VARCHAR(20), country VARCHAR(100), edit_date TIMESTAMP WITH TIME ZONE, CONSTRAINT fk_contact_details_employee FOREIGN KEY (employee_id) REFERENCES employees (id) )

-- Create indexes for faster queries
CREATE INDEX idx_employee_supervisor ON employees (supervisor_id);
CREATE INDEX idx_employee_status ON employees (employee_status);
CREATE INDEX idx_employee_role ON employees (employee_role);
CREATE INDEX idx_employee_contact_email ON employee_contact_details (email)

ALTER TABLE public.employees
    ALTER COLUMN id SET GENERATED BY DEFAULT;
ALTER TABLE public.employee_credentials
    ALTER COLUMN id SET GENERATED BY DEFAULT;
ALTER TABLE public.employee_contact_details
    ALTER COLUMN id SET GENERATED BY DEFAULT

ALTER SEQUENCE employees_id_seq RESTART WITH 1;
ALTER SEQUENCE employee_credentials_id_seq RESTART WITH 1;
ALTER SEQUENCE employee_contact_details_id_seq RESTART WITH 1

SELECT setval('employees_id_seq', 4);
SELECT setval('employee_credentials_id_seq', 4);
SELECT setval('employee_contact_details_id_seq', 4)
